function Dashbord(){
 const accData =[
        {
            'Name': "Maxwel Omondi",
            'AccNumber': '012342566',
            'accBallance':3545.25
        },
        {
            'Name': "Tyrany Of Nm",
            'AccNumber': '012342568',
            'accBallance':35455.25
        },
        {
            'Name': "Malcom X",
            'AccNumber': '012342567',
            'accBallance':35885.25
        },
        {
            'Name': "John X",
            'AccNumber': '012342565',
            'accBallance':35545.25
        }
 ]

    return(
    <>
        {/* the left-side Menu */}
        <Div id='left-side-menu'>

        </Div>


        {/* table showing all members with their Ballances */}
        <table className=" table table-dark table-bordered">
        <tbody>
        <tr>
                <th>Number</th>
                <th>ACC NUMBER</th>
                <th>NAME</th>
                <th>ACC BALLANCE</th>
        </tr>
         {accData.map((data,i)=>

            <tr key ={i}>
                <td>{i+1}</td>
                <td >{data.AccNumber}</td>
                <td>{data.Name}</td>
                <td>{data.accBallance}</td>
            </tr>
         )}
         </tbody>
        </table>
        
    </>
)}

export default Dashbord;
