import { useEffect, useState } from "react";
import logo2 from '../media/logo2.png'

import './css/login.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import Logo1 from "./logo1";



function LoginPage(){


    const [userName, setUserName]=useState('')
    const [pasword, setPasword]=useState('')

    const handleSubmit = ()=>{
        // InputValidation
        
    }

    return( 
        <section className="vh-100">
        
        <div className="container-fluid">

        {/* Adding the Logo Component */}
       
        <Logo1/>

        {/* addint the other Components */}
          <div className="row w-100 vh-100">
            <div className="col-sm-6 text-black">
      
              <div className="d-flex align-items-center h-custom-2 px-5 ms-xl-4 mt-5 pt-5 pt-xl-0 mt-xl-n5">
      
                <form style={{width: "23rem"}}>
      
                  <h3 className="fw-normal mb-3 pb-3" style={{letterSpacing: '1px'}}>Log in</h3>
      
                  <div className="form-outline mb-4">
                    <input type="email" 
                        id="form2Example18" 
                        className="form-control form-control-lg" 
                        onChange={(e)=>setUserName(e.target.value)}
                        />
                    <label className="form-label" htmlFor="form2Example18">
                        {userName.length > 0? userName : 'Email address'}
                        </label>
                  </div>
      
                  <div className="form-outline mb-4">
                    <input type="password" id="form2Example28" 
                        className="form-control 
                        form-control-lg" 
                        onChange={(e)=>{
                            setPasword(e.target.value)
                        }}/>
                    <label className="form-label" htmlFor="form2Example28">Password</label>
                  </div>
      
                  <div className="pt-1 mb-4">
                    <button className="btn btn-info btn-lg btn-block" 
                        type="submit"
                        onClick={e=>e.preventDefault()}>Login
                    </button>
                  </div>
      
                  <p className="small mb-5 pb-lg-2">
                    <a className="text-muted" href="#!">Forgot password?
                    </a>
                   </p>
                  <p>Want To join? <a href="#!" 
                    className="link-info">Submit Registration Request 
                    </a> here
                  </p>
      
                </form>
      
              </div>
      
            </div>

            {/* the side Logo */}
            <div className="col-sm-6 px-0 d-none d-sm-block">
              <img src={logo2} 
              alt="Login image" className="w-75 vh-50" 
              style={{backgroundColor:'none', objectFit: 'cover', objectPosition: 'left', filter: "contrast(170%)"}}/>
            </div>
          </div>
        </div>
        </section>


    )
    
    
};


export default LoginPage;
