import { useEffect, useState } from "react";
import Logo1 from "./logo1";



function Dashbord(){
    const[bankData, setBankData]=useState([])

    const fetchdata = () => {
        fetch("/api/dashbord")
            .then(response => 
             response.json()
            )
            .then(data => {
            setBankData(data)
              })
          }
    useEffect(()=>{
        fetchdata()
        }

    ,[])
    
        console.log(bankData)

    return(
    <>
        {/* the left-side Menu */}
        <div id='left-side-menu'>

        </div>
        <Logo1/>

        {/* table showing all members with their Ballances */}
        <table className=" table table-dark table-bordered">
        <tbody>
        <tr>
                <th>Number</th>
                <th>ACC NUMBER</th>
                <th>NAME</th>
                <th>ACC BALLANCE</th>
        </tr>
         {bankData.map((data,i)=>

            <tr key ={i}>
                <td>{i+1}</td>
                <td >{bankData.AccNumber}</td>
                <td>{bankData.Name}</td>
                <td>{bankData.accBallance}</td>
            </tr>
         )}
         </tbody>
        </table>
        
    </>
)}

export default Dashbord;
